FROM timer_info t
JOIN routine_info r ON r.timer_info_id = t.id


	WHERE t.${key} LIKE concat('%', #{word}, '%')


	ORDER BY ${orderBy} ${orderByDir}

GROUP BY t.id, t.user_id, t.title, t.views_count, t.complete_count, t.level, t.created_at;
